import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import { fontFamily, textAlign, fontWeight } from '@material-ui/system';
import './upper.css';
import axios from 'axios';

const useStyles = makeStyles(theme => ({
    root: {
      fontFamily: "BebasNeueRegular",
      paddingTop: theme.spacing(10),
      paddingLeft: theme.spacing(9),
      paddingBottom: theme.spacing(15),
      background: 'linear-gradient(to top, #000000, #2a1219, #501a28, #771f35, #a1243f, #b02740, #bf2c3f, #ce313e, #c53438, #bc3632, #b4382c, #ab3927)',
      color: 'white'
  
    },
    row:{
      marginTop: theme.spacing(1),
    },
  
    imgRow:{
      marginTop: theme.spacing(3),
      marginLeft: theme.spacing(3),
    },
    paper:{
      textAlign: "left",
      justifyContent: 'center',
      // background: 'blue'
      
  
    },
  
    paperContent:{
      fontWeight: "bold",
      fontSize: "20px",
    },
  
    leftPart:{
      paddingTop: theme.spacing(5)
    },
  
    img:{
      marginLeft: theme.spacing(5)
    },
  }));


class App extends React.Component {
    render() {
      return (
        <div className="Artists">
          <Grid item xs={8}>
                <Grid item xs={12} className={classes.imgRow}>
                    <div>
                        <img src={Imagine} alt='Imagine Dragons' width="150px" height="150px" />
                        <img className={classes.img} src={Chain} alt='Chain Smokers' width="150px" height="150px" />
                        <img className={classes.img} src={Marshmello} alt='Marshmello' width="150px" height="150px" />
                    </div>
                </Grid>

                <Grid item xs={12} className={classes.imgRow}>
                    <div className={classes.paperImg}>
                        <img src={Coldplay} alt='Coldplay' width="150px" height="150px" />
                        <img className={classes.img} src={Avicii} alt='Avicii RIP' width="150px" height="150px" />
                        <img className={classes.img} src={OneRepublic} alt='One Republic' width="150px" height="150px" />
                    </div>
                </Grid>
            </Grid>
        </div>
      );
    }

componentDidMount() {
    this.getData()
  }

  getData() {
    axios({
      url: 'http://localhost:8000/graphql/',
      method: 'post',
      data: {
        query: `
        {
          allArtist {
            name
          }
        }
          `
      }
    }).then((response) => {

        console.log(response)
    //   this.setMusicData(response.data.data.allMusic[0]);

    }).catch((error)=>{
      console.log(error)
      document.getElementById('header').innerHTML="Api not answering"
    });
  };

  setMusicData(response)
  {
    let albums = response.albums.split(',');
    let artists = response.artists.split(',');
    let songs = response.songs.split(',');

    let albumsElement = document.getElementById('albums');
    let artistsElement = document.getElementById('artists');
    let songsElement = document.getElementById('songs');


    albums.forEach(album => {
      let temp = document.createElement('li');
      temp.innerHTML = "<li>" + album + "</li>";
      albumsElement.appendChild(temp)
    });


    artists.forEach(artist => {
      let temp = document.createElement('li');
      temp.innerHTML = "<li>" + artist + "</li>";
      artistsElement.appendChild(temp)
    });

    songs.forEach(song => {
      let temp = document.createElement('li');
      temp.innerHTML = "<li>" + song + "</li>";
      songsElement.appendChild(temp)
    });
  }
}

export default Artists;
