import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import './upper.css';
import axios from 'axios';
import { arrayOf } from 'prop-types';
import Marshmello from './img/marshmello.jpg'
import Upper from './upper';




class Artists extends React.Component {
    constructor(){
        super();
        this.classes = makeStyles(theme => ({
            root: {
              fontFamily: "BebasNeueRegular",
              paddingTop: theme.spacing(10),
              paddingLeft: theme.spacing(9),
              paddingBottom: theme.spacing(15),
              background: 'linear-gradient(to top, #000000, #2a1219, #501a28, #771f35, #a1243f, #b02740, #bf2c3f, #ce313e, #c53438, #bc3632, #b4382c, #ab3927)',
              color: 'white'
          
            },
            row:{
              marginTop: theme.spacing(1),
            },
          
            imgRow:{
              marginTop: theme.spacing(3),
              marginLeft: theme.spacing(3),
            },
            paper:{
              textAlign: "left",
              justifyContent: 'center',
              // background: 'blue'
              
          
            },
          
            paperContent:{
              fontWeight: "bold",
              fontSize: "20px",
            },
          
            leftPart:{
              paddingTop: theme.spacing(5)
            },
          
            img:{
              marginLeft: theme.spacing(5)
            },
          }));

          this.getData()
    }

  getData() {
    axios({
      url: 'http://localhost:8000/graphql/',
      method: 'post',
      data: {
        query: `
        {
          allArtist {
            name
          }
        }
          `
      }
    }).then((response) => {

        let allArtists =response.data.data.allArtist 
        console.log(allArtists[0])
        this.artist = allArtists

    }).catch((error)=>{
      console.log(error)
    });
  };


    render() {
      return (
        <Upper artists={this.artist} />
      );
    }
  }


export default Artists
