import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import './upper.css';
import axios from 'axios';
import { arrayOf } from 'prop-types';




class Artists extends React.Component {
    constructor(){
        super();
        this.classes = makeStyles(theme => ({
            root: {
              fontFamily: "BebasNeueRegular",
              paddingTop: theme.spacing(10),
              paddingLeft: theme.spacing(9),
              paddingBottom: theme.spacing(15),
              background: 'linear-gradient(to top, #000000, #2a1219, #501a28, #771f35, #a1243f, #b02740, #bf2c3f, #ce313e, #c53438, #bc3632, #b4382c, #ab3927)',
              color: 'white'
          
            },
            row:{
              marginTop: theme.spacing(1),
            },
          
            imgRow:{
              marginTop: theme.spacing(3),
              marginLeft: theme.spacing(3),
            },
            paper:{
              textAlign: "left",
              justifyContent: 'center',
              // background: 'blue'
              
          
            },
          
            paperContent:{
              fontWeight: "bold",
              fontSize: "20px",
            },
          
            leftPart:{
              paddingTop: theme.spacing(5)
            },
          
            img:{
              marginLeft: theme.spacing(5)
            },
          }));

          this.getData()
    }

  getData() {
    axios({
      url: 'http://localhost:8000/graphql/',
      method: 'post',
      data: {
        query: `
        {
          allArtist {
            name
          }
        }
          `
      }
    }).then((response) => {

        let allArtists =response.data.data.allArtist 
        this.setMusicData(allArtists);

    }).catch((error)=>{
      console.log(error)
      document.getElementById('artists').innerHTML="Api not answering"
    });
  };

  setMusicData(response)
  {

    // PartOne
      for(let i = 0; i< 3;i++){
        let temp = document.createElement('img');
        // temp.setAttribute("className", "{this.classes.img}"); 
        temp.setAttribute("src", "./img/" + response[i].name); 
        document.getElementById('partOne').appendChild(temp);
      }
    // PartTwo
    for(let i = 3; i< response.length;i++){
        console.log(response[i].name);
      }
    
  }

    render() {
      return (
        <div id='artists' className="artists">
          <Grid item xs={8}>
                <Grid item xs={12} className={this.classes.imgRow}>
                    <div id='partOne'>
                        {/* <img src={Imagine} alt='Imagine Dragons' width="150px" height="150px" />
                        <img className={this.classes.img} src={Chain} alt='Chain Smokers' width="150px" height="150px" />
                        <img className={this.classes.img} src={Marshmello} alt='Marshmello' width="150px" height="150px" /> */}
                    </div>
                </Grid>

                <Grid item xs={12} className={this.classes.imgRow}>
                <div id='partTwo' className={this.classes.paperImg}>
                        {/* <img src={Coldplay} alt='Coldplay' width="150px" height="150px" />
                        <img className={this.classes.img} src={Avicii} alt='Avicii RIP' width="150px" height="150px" />
                        <img className={this.classes.img} src={OneRepublic} alt='One Republic' width="150px" height="150px" /> */}
                    </div>
                </Grid>
            </Grid>
        </div>
      );
    }
  }


export default Artists
